$("#firstTown").on({
    mouseenter: function(){
      $("#firstTownText").fadeIn("slow");
      $("#firstTownImage").hide();
    },
    mouseleave: function(){
      $("#firstTownImage").fadeIn("slow");
      $("#firstTownText").hide();
    },
  });

